# 创建更大的图形
plt.figure(figsize=(32, 28))  # 增大图形尺寸
G = nx.Graph()

# 添加节点和位置
pos = {}
for node in graph_data['nodes']:
    G.add_node(node['id'])
    pos[node['id']] = (node['position'][0], node['position'][1])

# 添加边
for edge in graph_data['edges']:
    G.add_edge(edge['source'], edge['target'], label=edge['type'].strip())

# 绘制节点 (增大节点大小)
nx.draw_networkx_nodes(G, pos, node_size=1200, node_color='lightblue', alpha=0.9, edgecolors='gray')

# 绘制边 (加粗边线)
nx.draw_networkx_edges(G, pos, width=2.0, alpha=0.6, edge_color='gray')

# 绘制节点标签 (增大字体)
node_labels = {node: node.replace('_', '\n') for node in G.nodes()}  # 将_替换为换行
nx.draw_networkx_labels(G, pos, labels=node_labels, font_size=14, font_weight='bold', font_family='sans-serif')

# 绘制边标签 (增大字体)
edge_labels = nx.get_edge_attributes(G, 'label')
nx.draw_networkx_edge_labels(G, pos, edge_labels=edge_labels, font_size=20, font_family='sans-serif')

# 调整布局
plt.title('Room Layout Visualization', fontsize=16)
plt.axis('off')
plt.tight_layout()

# 显示图形
plt.show()